class IPLocation
{
    /**
     * 获取归属地
     * @param string $name 需要解析的字符串
     * @return array [$province_id, $city_id, $province_name, $city_name, $district_name]
     */
    public static function getLocation($district_name)
    {
        $province_id = $city_id = 0;
        foreach (static::commonProvince() as $pid => $pname) {
            if (false !== strpos($district_name, $pname)) {
                $province_id = $pid;
                break;
            }
        }
        //35=海外
        foreach ([$province_id, 35] as $location_id) {
            if ($location_id > 0) {
                foreach (static::provinceCitys($location_id) as $cid => $cname) {
                    if (false !== strpos($district_name, $cname)) {
                        $city_id = $cid;
                        break 2;
                    }
                }
            }
        }

        return [
            $province_id,
            $city_id,
            static::getName($province_id),
            static::getName($city_id),
            $district_name
        ];
    }

    /**
     * 获取地区列表
     * @return array
     */
    public static function commonDistrict()
    {
        $districts = [];
        foreach (static::getLocationConfigurations() as $location_id => $district) {
            $districts[$location_id] = $district[0];
        }
        return $districts;
    }

    /**
     * 获取省份列表
     *  @return array
     */
    public static function commonProvince()
    {
        $province_list = [];
        foreach (static::getLocationConfigurations() as $location_id => $district) {
            if ($district[2] === 1) {
                $province_list[$location_id] = $district[0];
            } else {
                break;
            }
        }
        return $province_list;
    }

    /**
     * 获取省份城市列表
     * @return array
     */
    public static function provinceCitys($location_id = 0)
    {
        $province_citys = [];
        foreach (static::getLocationConfigurations() as $location_id => $district) {
            if ($district[2] === 1) {
                $province_citys[$location_id] = [];
            } else {
                $province_citys[$district[1]][$location_id] = $district[0];
            }
        }
        return $location_id > 0 ? (isset($province_citys[$location_id]) ? $province_citys[$location_id] : []) : $province_citys;
    }

    /**
     * 根据ID获取名称
     * @param int $location_id
     * @return string $district_name
     */
    public static function getName($location_id)
    {
        $district = static::getLocationConfigurations();
        return isset($district[$location_id]) ? $district[0] : '未知';
    }

